def is_valid_message(text):
    text = text.strip()
    if len(text) < 5 or len(text) > 300:
        return False
    if re.fullmatch(r'[\d\s\W]+', text):
        return False
    if re.search(r'https?://|www\.|\.com|\.ir|\.net|rubika\.ir|@\w+', text, re.IGNORECASE):
        return False#
    spam_keywords = ['Ø¹Ø¶Ùˆ Ø´Ùˆ', 'ÙØ§Ù„Ùˆ Ú©Ù†', 'Ú©Ø§Ù†Ø§Ù„ Ù…Ø§', 'Ù„ÛŒÙ†Ú© Ú¯Ø±ÙˆÙ‡', 'Ú©Ø³Ø¨ Ø¯Ø±Ø¢Ù…Ø¯', 'Ø±Ø§ÛŒ Ø¨Ø¯Ù‡']
    if any(word in text.lower() for word in spam_keywords):
        return False
    if re.search(r'(.)\1{4,}', text):
        return False
    if re.fullmatch(r'[a-zA-Z0-9\s.,!?@#]+', text):  # ÙÛŒÙ†Ú¯Ù„ÛŒØ´
        return False
    if re.fullmatch(r'[^\w\s]+', text):  # ÙÙ‚Ø· Ø§ÛŒÙ…ÙˆØ¬ÛŒ ÛŒØ§ Ø¹Ù„Ø§Ù…Øª
        return False
    return True

